import React from "react";
import Layout from "../containers/Layout";

export default ({ statusCode }) => (
  <Layout>
    <p>
      {statusCode
        ? `An error ${statusCode} occured on Server`
        : `an error occured on Client`}
    </p>
  </Layout>
);

